import React from 'react'

import "../css/NurseBotStyle.css"

const NurseBot = () => {
    const handleSubmit = (e) => {
        e.preventDefault();
        var inp = document.getElementById("inp_message").value;
        var box = document.getElementById("box");
        
        box.innerHTML += '<div class="msg-right">'+inp+'</div>';

        const question1 = ["hi","hello","hey"]
        const answer1 = "hello sir how might i help you";
        const questions = [question1]
        const answers = [answer1]
        questions.forEach((Q,i)=>{
            Q.forEach(q1=>{
                if(q1.toLowerCase==inp){
                    box.innerHTML += '<div class="msg-left">'+answers[i]+'</div>';
                    

                }
            })
        })

        if(inp = "hi"){
            box.innerHTML += '<div class="msg-left">'+"hello sir how might i help you"+'</div>';

        }


    }
    return (
        <div>

                <center>
                   
                    <h1>Chatting app</h1>
                   


  


                </center>
                <div id="box"><div class="msg-left">sample text</div></div>

                <form id="send-container">

                    <input type="text" id="inp_message"/>
                        <button id="addbtn" className="button" onClick={handleSubmit}> Send </button>

                </form>
             

                </div>
                    )
}

export default NurseBot
